import React, { useState } from "react";
import Header from "./common/Header";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const onSubmit = async (data) => {
    setLoading(true);

    try {
      const response = await axios.post("http://localhost:4000/login", {
        email: data.email,
        password: data.password,
      });

      if (response.status === 200) {
        const { user, userId, email, role, accessToken, refreshToken } =
          response.data;

        if (role === "User") {
          // Đăng nhập thành công cho người dùng
          localStorage.setItem("accessToken", accessToken);
          localStorage.setItem("refreshToken", refreshToken);

          dispatch(login(user, userId, email, role, accessToken, refreshToken));

          reset();
          navigate("/");
        } else if (role === "Seller") {
          // Hiển thị thông báo không có quyền đăng nhập
          setError(
            "Bạn là Nhà bán hàng. Hãy truy cập vào trang đăng nhập dành riêng cho Nhà bán hàng."
          );
        } else if (role === "Admin") {
          // Hiển thị thông báo không có quyền đăng nhập
          setError(
            "Bạn là người quản lý. Hãy truy cập vào trang đăng nhập dành riêng cho Người quản lý."
          );
        }
      }
    } catch (err) {
      // Xử lý lỗi
      if (err.response.status === 400) {
        setError(err.response.data.error);
      } else {
        setError("Something went wrong!");
      }
    }

    setLoading(false);
  };

  return (
    <>
      <Header />
      <section className="bg-them-gray mt-40">
        <div className="w-[1280px] h-auto m-auto flex items-center justify-between">
          <div className="w-full flex flex-col justify-center items-center">
            <div>
              <h2>Chào mừng bạn đến với chúng tôi</h2>
            </div>
            <form onSubmit={handleLogin}>
              <div className="relative">
                <input
                  type="text"
                  className="w-[400px] h-[50px] px-3 border"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Email"
                />
                <label
                  className="absolute top-1/2 left-3 text-[14px] -translate-y-1/2 bg-white px-1 text-text7777 pointer-events-none"
                  htmlFor="email"
                >
                  Email
                </label>
              </div>
              <div className="relative">
                <input
                  type="password"
                  className="w-[400px] h-[50px] px-3 border"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Password"
                />
                <label
                  className="absolute top-1/2 left-3 text-[14px] -translate-y-1/2 bg-white px-1 text-text7777 pointer-events-none"
                  htmlFor="password"
                >
                  Password
                </label>
              </div>

              <div className="flex justify-center bg-theme-color">
                <button type="submit" className="btn btn-primary">
                  Login
                </button>
              </div>
            </form>
          </div>
        </div>
      </section>
    </>
  );
}
