import React, { useState } from "react";
import Header from "./common/Header";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    try {
      const response = await axios.post("http://localhost:4000/login", {
        username,
        password,
      });

      // Xử lý khi đăng nhập thành công, có thể chuyển hướng trang hoặc thực hiện các thao tác khác
      console.log("Login successful:", response.data);
    } catch (error) {
      // Xử lý khi đăng nhập thất bại, hiển thị thông báo hoặc thực hiện các thao tác khác
      console.error("Login failed:", error.message);
    }
  };

  return (
    <>
      <Header />
      <section className="bg-them-gray mt-40">
        <div className="w-[1280px] h-auto m-auto flex items-center justify-between">
          <div className="w-full flex flex-col justify-center items-center">
            <div>
              <h2>Chào mừng bạn đến với chúng tôi</h2>
            </div>
            <form onSubmit={handleLogin}>
              <div className="relative">
                <input
                  type="text"
                  className="w-[400px] h-[50px] px-3 border"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Email"
                />
                <label
                  className="absolute top-1/2 left-3 text-[14px] -translate-y-1/2 bg-white px-1 text-text7777 pointer-events-none"
                  htmlFor="email"
                >
                  Email
                </label>
              </div>
              <div className="relative">
                <input
                  type="password"
                  className="w-[400px] h-[50px] px-3 border"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Password"
                />
                <label
                  className="absolute top-1/2 left-3 text-[14px] -translate-y-1/2 bg-white px-1 text-text7777 pointer-events-none"
                  htmlFor="password"
                >
                  Password
                </label>
              </div>

              <div className="flex justify-center bg-theme-color">
                <button type="submit" className="btn btn-primary">
                  Login
                </button>
              </div>
            </form>
          </div>
        </div>
      </section>
    </>
  );
}
